


public class ExNestedWhile {



	public static void main(String[] args) {

		// TODO Auto-generated method stub
     int i=1,j;
     while(i<=5)
     {
		 j=i;
		 while(j<=5)
		 {
			 System.out.print(j);
			 j++;
		}

    	 System.out.println();
    	 i++;
     }
	}

}
